import React, { useState } from 'react';
import axios from 'axios';
import { Container, Typography, TextField, Button, Box } from '@mui/material';

export default function AskQuestion() {
  const [file, setFile] = useState(null);
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [context, setContext] = useState('');
  const [loading, setLoading] = useState(false);

  const handleUpload = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('file', file);

    setLoading(true);

    try {
      const response = await axios.post('http://localhost:3000/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      setContext(response.data.extractedText);
    } catch (error) {
      console.error('Erro ao fazer upload:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleQuestionSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await axios.post('http://localhost:3000/ask', {
        question,
        context,
      });

      setAnswer(response.data.answer);
    } catch (error) {
      console.error('Erro ao enviar a pergunta:', error);
      setAnswer('Desculpe, houve um erro ao processar sua pergunta.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container>
      <Typography variant="h5" gutterBottom>
        Envie um boleto e faça perguntas sobre ele
      </Typography>

      <form onSubmit={handleUpload}>
        <TextField
          type="file"
          onChange={(e) => setFile(e.target.files[0])}
          fullWidth
          required
        />
        <Button type="submit" variant="contained" color="primary" disabled={loading}>
          {loading ? 'Enviando...' : 'Enviar Boleto'}
        </Button>
      </form>

      {context && (
        <Box mt={4}>
          <Typography variant="h6">Conteúdo Extraído:</Typography>
          <Typography>{context}</Typography>

          <form onSubmit={handleQuestionSubmit}>
            <TextField
              label="Pergunta"
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              fullWidth
              required
            />
            <Button type="submit" variant="contained" color="secondary" disabled={loading}>
              {loading ? 'Perguntando...' : 'Enviar Pergunta'}
            </Button>
          </form>

          {answer && (
            <Box mt={4}>
              <Typography variant="h6">Resposta:</Typography>
              <Typography>{answer}</Typography>
            </Box>
          )}
        </Box>
      )}
    </Container>
  );
}
