import React, { useState } from 'react';
import axios from 'axios';

export default function AskQuestion() {
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [context, setContext] = useState(''); // O texto extraído do boleto, que você pode passar ao backend

  const handleQuestionSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3000/ask', {
        question,
        context, // Envia o conteúdo extraído do boleto junto com a pergunta
      });

      setAnswer(response.data.answer); // Exibe a resposta gerada pela LLM
    } catch (error) {
      console.error('Erro ao enviar a pergunta:', error);
      setAnswer('Desculpe, houve um erro ao processar sua pergunta.');
    }
  };

  return (
    <div>
      <h1>Faça uma pergunta sobre o boleto</h1>
      <form onSubmit={handleQuestionSubmit}>
        <textarea
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          placeholder="Digite sua pergunta"
        />
        <button type="submit">Enviar</button>
      </form>
      {answer && <div><h3>Resposta:</h3><p>{answer}</p></div>}
    </div>
  );
}
